import React, { Component } from "react";

//counter
class Counter extends React.Component {
  state = {
    count: 0,
    image
  };

  render() {
    return (
      <React.Fragment>
          <img src="" alt="" />
        <span>{this.formatCount()}</span>
        <button>press me</button>
      </React.Fragment>
    );
  }

  formatCount() {
    const { count } = this.state;
    return count === 0 ? "zero" : count;
  }
}

export default Counter;
